INSERT INTO `faqs` VALUES (3, '{\"en\":\"How can I order a painting from an artist?\",\"uk\":\"\\u042f\\u043a \\u044f \\u043c\\u043e\\u0436\\u0443 \\u0437\\u0430\\u043c\\u043e\\u0432\\u0438\\u0442\\u0438 \\u043a\\u0430\\u0440\\u0442\\u0438\\u043d\\u0443 \\u0443 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430?\"}', '{\"en\":\"You will see the \\\"Order Artwork\\\" button in the shop window and in the artwork listings of any artist who accepts orders. Don\'t see this button? You can contact the artist directly to see if they are willing to accept orders. Just click the \\\"Contact Us\\\" button on their store.\",\"uk\":\"\\u0412\\u0438 \\u043f\\u043e\\u0431\\u0430\\u0447\\u0438\\u0442\\u0435 \\u043a\\u043d\\u043e\\u043f\\u043a\\u0443 \\u00ab\\u0417\\u0430\\u043c\\u043e\\u0432\\u0438\\u0442\\u0438 \\u0442\\u0432\\u043e\\u0440\\u0438 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430\\u00bb \\u043d\\u0430 \\u0432\\u0456\\u0442\\u0440\\u0438\\u043d\\u0456 \\u043c\\u0430\\u0433\\u0430\\u0437\\u0438\\u043d\\u0443 \\u0442\\u0430 \\u0432 \\u0441\\u043f\\u0438\\u0441\\u043a\\u0430\\u0445 \\u0442\\u0432\\u043e\\u0440\\u0456\\u0432 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430 \\u0431\\u0443\\u0434\\u044c-\\u044f\\u043a\\u043e\\u0433\\u043e \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430, \\u044f\\u043a\\u0438\\u0439 \\u043f\\u0440\\u0438\\u0439\\u043c\\u0430\\u0454 \\u0437\\u0430\\u043c\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f. \\u041d\\u0435 \\u0431\\u0430\\u0447\\u0438\\u0442\\u0435 \\u0446\\u044e \\u043a\\u043d\\u043e\\u043f\\u043a\\u0443? \\u0412\\u0438 \\u043c\\u043e\\u0436\\u0435\\u0442\\u0435 \\u0437\\u0432\\u2019\\u044f\\u0437\\u0430\\u0442\\u0438\\u0441\\u044f \\u0431\\u0435\\u0437\\u043f\\u043e\\u0441\\u0435\\u0440\\u0435\\u0434\\u043d\\u044c\\u043e \\u0437 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u043e\\u043c, \\u0449\\u043e\\u0431 \\u0434\\u0456\\u0437\\u043d\\u0430\\u0442\\u0438\\u0441\\u044f, \\u0447\\u0438 \\u0433\\u043e\\u0442\\u043e\\u0432\\u0438\\u0439 \\u0432\\u0456\\u043d \\u043f\\u0440\\u0438\\u0439\\u043c\\u0430\\u0442\\u0438 \\u0437\\u0430\\u043c\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f. \\u041f\\u0440\\u043e\\u0441\\u0442\\u043e \\u043d\\u0430\\u0442\\u0438\\u0441\\u043d\\u0456\\u0442\\u044c \\u043a\\u043d\\u043e\\u043f\\u043a\\u0443 \\u00ab\\u0417\\u0432\\u2019\\u044f\\u0437\\u0430\\u0442\\u0438\\u0441\\u044f\\u00bb \\u0432 \\u0457\\u0445 \\u043c\\u0430\\u0433\\u0430\\u0437\\u0438\\u043d\\u0456.\"}', 0, '2022-10-26 14:43:41', '2022-10-26 14:43:41');
INSERT INTO `faqs` VALUES (4, '{\"en\":\"How do I pay for a custom artwork?\",\"uk\":\"\\u042f\\u043a \\u044f \\u043e\\u043f\\u043b\\u0430\\u0442\\u0438\\u0442\\u0438 \\u0432\\u0438\\u0442\\u0432\\u0456\\u0440 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430 \\u043d\\u0430 \\u0437\\u0430\\u043c\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f?\"}', '{\"en\":\"Once the artist agrees to take your order, a member of the Artfinder team will contact you via email to take a non-refundable deposit. This deposit is 30-50% of the final value of the artwork, as agreed between you and the artist.\",\"uk\":\"\\u041a\\u043e\\u043b\\u0438 \\u043c\\u0438\\u0442\\u0435\\u0446\\u044c \\u043f\\u043e\\u0433\\u043e\\u0434\\u0438\\u0442\\u044c\\u0441\\u044f \\u0432\\u0437\\u044f\\u0442\\u0438 \\u0432\\u0430\\u0448\\u0435 \\u0437\\u0430\\u043c\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f, \\u0447\\u043b\\u0435\\u043d \\u043a\\u043e\\u043c\\u0430\\u043d\\u0434\\u0438 Artfinder \\u0437\\u0432\\u2019\\u044f\\u0436\\u0435\\u0442\\u044c\\u0441\\u044f \\u0435\\u043b\\u0435\\u043a\\u0442\\u0440\\u043e\\u043d\\u043d\\u043e\\u044e \\u043f\\u043e\\u0448\\u0442\\u043e\\u044e, \\u0449\\u043e\\u0431 \\u0432\\u0437\\u044f\\u0442\\u0438 \\u0434\\u0435\\u043f\\u043e\\u0437\\u0438\\u0442, \\u044f\\u043a\\u0438\\u0439 \\u043d\\u0435 \\u043f\\u043e\\u0432\\u0435\\u0440\\u0442\\u0430\\u0454\\u0442\\u044c\\u0441\\u044f. \\u0426\\u0435\\u0439 \\u0434\\u0435\\u043f\\u043e\\u0437\\u0438\\u0442 \\u0441\\u0442\\u0430\\u043d\\u043e\\u0432\\u0438\\u0442\\u044c 30-50% \\u0432\\u0456\\u0434 \\u043e\\u0441\\u0442\\u0430\\u0442\\u043e\\u0447\\u043d\\u043e\\u0457 \\u0432\\u0430\\u0440\\u0442\\u043e\\u0441\\u0442\\u0456 \\u0442\\u0432\\u043e\\u0440\\u0443 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430, \\u0437\\u0430 \\u0434\\u043e\\u043c\\u043e\\u0432\\u043b\\u0435\\u043d\\u0456\\u0441\\u0442\\u044e \\u043c\\u0456\\u0436 \\u0432\\u0430\\u043c\\u0438 \\u0442\\u0430 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u043e\\u043c.\"}', 0, '2022-10-26 14:45:39', '2022-10-26 14:45:39');
INSERT INTO `ideas` VALUES (6, '{\"en\":\"About idea\",\"uk\":\"\\u041f\\u0440\\u043e i\\u0434\\u0435\\u044e\"}', '{\"en\":\"<p>The idea is dynamic, like life itself - born, matures, overflows in the mind, sparks, does not give rest, urges to action, wants fixation, grounding, static, stating - for centuries, in matter, in the flesh. It absorbs you, rightes its progenitor, subdues, leads... everything - you are not outside the idea, you are merged, one charge - you are the conductor, she is the current.<br><\\/p>\",\"uk\":\"<p>\\u0406\\u0434\\u0435\\u044f \\u0434\\u0438\\u043d\\u0430\\u043c\\u0456\\u0447\\u043d\\u0430, \\u044f\\u043a \\u0441\\u0430\\u043c\\u0435 \\u0436\\u0438\\u0442\\u0442\\u044f, - \\u043d\\u0430\\u0440\\u043e\\u0434\\u0436\\u0443\\u0454\\u0442\\u044c\\u0441\\u044f, \\u0437\\u0440\\u0456\\u0454, \\u043f\\u0435\\u0440\\u0435\\u043b\\u0438\\u0432\\u0430\\u0454\\u0442\\u044c\\u0441\\u044f \\u0443 \\u0441\\u0432\\u0456\\u0434\\u043e\\u043c\\u043e\\u0441\\u0442\\u0456, \\u0456\\u0441\\u043a\\u0440\\u0438\\u0442\\u044c, \\u043d\\u0435 \\u0434\\u0430\\u0454 \\u0441\\u043f\\u043e\\u043a\\u043e\\u044e, \\u0441\\u043f\\u043e\\u043d\\u0443\\u043a\\u0430\\u0454 \\u0434\\u043e \\u0434\\u0456\\u0457, \\u0445\\u043e\\u0447\\u0435 \\u0444\\u0456\\u043a\\u0441\\u0430\\u0446\\u0456\\u0457, \\u0437\\u0430\\u0437\\u0435\\u043c\\u043b\\u0435\\u043d\\u043d\\u044f, \\u0441\\u0442\\u0430\\u0442\\u0438\\u043a\\u0438, \\u043a\\u043e\\u043d\\u0441\\u0442\\u0430\\u0442\\u0430\\u0446\\u0456\\u0457 - \\u043d\\u0430 \\u0432\\u0456\\u043a\\u0438, \\u0443 \\u043c\\u0430\\u0442\\u0435\\u0440\\u0456\\u0457, \\u0443 \\u043f\\u043b\\u043e\\u0442\\u0456. \\u0412\\u043e\\u043d\\u0430 \\u043f\\u043e\\u0433\\u043b\\u0438\\u043d\\u0430\\u0454 \\u0442\\u0435\\u0431\\u0435, \\u043f\\u0440\\u0430\\u0432\\u043e\\u0441\\u0445\\u043e\\u0434\\u0438\\u0442\\u044c \\u0441\\u0432\\u043e\\u0433\\u043e \\u0440\\u043e\\u0434\\u0438\\u0442\\u0435\\u043b\\u044f, \\u043f\\u0456\\u0434\\u043f\\u043e\\u0440\\u044f\\u0434\\u043a\\u043e\\u0432\\u0443\\u0454, \\u0432\\u0435\\u0434\\u0435... \\u0432\\u0441\\u0435 - \\u0442\\u0435\\u0431\\u0435 \\u043d\\u0435\\u043c\\u0430\\u0454 \\u043f\\u043e\\u0437\\u0430 \\u0456\\u0434\\u0435\\u0454\\u044e, \\u0432\\u0438 \\u0437\\u043b\\u0438\\u0442\\u0456, \\u0454\\u0434\\u0438\\u043d\\u0438\\u043c \\u0437\\u0430\\u0440\\u044f\\u0434\\u043e\\u043c - \\u0442\\u0438 \\u043f\\u0440\\u043e\\u0432\\u0456\\u0434\\u043d\\u0438\\u043a, \\u0432\\u043e\\u043d\\u0430 - \\u0441\\u0442\\u0440\\u0443\\u043c.<\\/p>\"}', '{\"en\":\"<p>about idea content<\\/p>\",\"uk\":\"<p>\\u043f\\u0440\\u043e \\u0456\\u0434\\u0435\\u044e \\u043a\\u043e\\u043d\\u0442\\u0435\\u043d\\u0442<\\/p>\"}', 'images/mwe7TiZlFotF0To0dYnbn437E11SgTFKikJzFmVJ.jpg', 100, '2022-11-20 22:33:55', '2022-11-20 22:33:55');
