INSERT INTO `language_lines` VALUES (150, 'about', 'header1', '{\"en\":\"We strive to show the world the art culture of Ukrainian artists\",\"ru\":\"\\u041c\\u0438 \\u043f\\u0440\\u0430\\u0433\\u043d\\u0435\\u043c\\u043e \\u043f\\u043e\\u043a\\u0430\\u0437\\u0430\\u0442\\u0438 \\u0441\\u0432\\u0456\\u0442\\u0443 \\u0430\\u0440\\u0442-\\u043a\\u0443\\u043b\\u044c\\u0442\\u0443\\u0440\\u0443 \\u0443\\u043a\\u0440\\u0430\\u0457\\u043d\\u0441\\u044c\\u043a\\u0438\\u0445 \\u043c\\u0438\\u0442\\u0446\\u0456\\u0432\",\"uk\":\"\\u041c\\u0438 \\u043f\\u0440\\u0430\\u0433\\u043d\\u0435\\u043c\\u043e \\u043f\\u043e\\u043a\\u0430\\u0437\\u0430\\u0442\\u0438 \\u0441\\u0432\\u0456\\u0442\\u0443 \\u0430\\u0440\\u0442-\\u043a\\u0443\\u043b\\u044c\\u0442\\u0443\\u0440\\u0443 \\u0443\\u043a\\u0440\\u0430\\u0457\\u043d\\u0441\\u044c\\u043a\\u0438\\u0445 \\u043c\\u0438\\u0442\\u0446\\u0456\\u0432\"}', '2023-01-27 13:31:54', '2023-01-27 13:31:54');
INSERT INTO `language_lines` VALUES (151, 'about', 'p1', '{\"en\":\"Our mission is to show Ukrainian art to the world and make it accessible. We help Ukrainian artists and cooperate with the National Union of Artists of Ukraine to present art at the world level. In this way, we help buyers find art that they cannot find anywhere else. We value what we do for our artists and clients, and stay true to who we are above all else.\",\"ru\":\"\\u041d\\u0430\\u0448\\u0430 \\u043c\\u0456\\u0441\\u0456\\u044f \\u043f\\u043e\\u043b\\u044f\\u0433\\u0430\\u0454 \\u0432 \\u0442\\u043e\\u043c\\u0443, \\u0449\\u043e\\u0431 \\u043f\\u043e\\u043a\\u0430\\u0437\\u0430\\u0442\\u0438 \\u0443\\u043a\\u0440\\u0430\\u0457\\u043d\\u0441\\u044c\\u043a\\u0435 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u043e \\u0441\\u0432\\u0456\\u0442\\u0443 \\u0442\\u0430 \\u0437\\u0440\\u043e\\u0431\\u0438\\u0442\\u0438 \\u0439\\u043e\\u0433\\u043e \\u0434\\u043e\\u0441\\u0442\\u0443\\u043f\\u043d\\u0438\\u043c. \\u041c\\u0438 \\u0434\\u043e\\u043f\\u043e\\u043c\\u0430\\u0433\\u0430\\u0454\\u043c\\u043e \\u0443\\u043a\\u0440\\u0430\\u0457\\u043d\\u0441\\u044c\\u043a\\u0438\\u043c \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430\\u043c \\u0442\\u0430 \\u0441\\u043f\\u0456\\u0432\\u043f\\u0440\\u0430\\u0446\\u044e\\u0454\\u043c\\u043e \\u0437 \\u041d\\u0430\\u0446\\u0456\\u043e\\u043d\\u0430\\u043b\\u044c\\u043d\\u043e\\u044e \\u0421\\u043f\\u0456\\u043b\\u043a\\u043e\\u044e \\u0425\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0456\\u0432 \\u0423\\u043a\\u0440\\u0430\\u0457\\u043d\\u0438, \\u043f\\u0440\\u0435\\u0434\\u0441\\u0442\\u0430\\u0432\\u0438\\u0442\\u0438 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u043e \\u043d\\u0430 \\u0441\\u0432\\u0456\\u0442\\u043e\\u0432\\u043e\\u043c\\u0443 \\u0440\\u0456\\u0432\\u043d\\u0456. \\u0422\\u0430\\u043a\\u0438\\u043c \\u0447\\u0438\\u043d\\u043e\\u043c \\u0434\\u043e\\u043f\\u043e\\u043c\\u0430\\u0433\\u0430\\u0