INSERT INTO `language_lines` VALUES (157, 'about', 'p4', '{\"en\":\"We launched Artwex to challenge the industry to offer a fair and sustainable marketplace for artists where anyone can find and buy original art. We strive to eliminate the \\\"starving artist\\\" syndrome and believe that affordable art and artist success should not be mutually exclusive.\",\"ru\":\"\\u041c\\u0438 \\u0437\\u0430\\u043f\\u0443\\u0441\\u0442\\u0438\\u043b\\u0438 Artwex, \\u0449\\u043e\\u0431 \\u043a\\u0438\\u043d\\u0443\\u0442\\u0438 \\u0432\\u0438\\u043a\\u043b\\u0438\\u043a \\u0433\\u0430\\u043b\\u0443\\u0437\\u0456, \\u0449\\u043e\\u0431 \\u0437\\u0430\\u043f\\u0440\\u043e\\u043f\\u043e\\u043d\\u0443\\u0432\\u0430\\u0442\\u0438 \\u0447\\u0435\\u0441\\u043d\\u0438\\u0439 \\u0456 \\u0441\\u0442\\u0456\\u0439\\u043a\\u0438\\u0439 \\u0440\\u0438\\u043d\\u043e\\u043a \\u0434\\u043b\\u044f \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0456\\u0432, \\u0434\\u0435 \\u0431\\u0443\\u0434\\u044c-\\u0445\\u0442\\u043e \\u043c\\u043e\\u0436\\u0435 \\u0437\\u043d\\u0430\\u0439\\u0442\\u0438 \\u0442\\u0430 \\u043a\\u0443\\u043f\\u0438\\u0442\\u0438 \\u043e\\u0440\\u0438\\u0433\\u0456\\u043d\\u0430\\u043b\\u044c\\u043d\\u0435 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u043e. \\u041c\\u0438 \\u043f\\u0440\\u0430\\u0433\\u043d\\u0435\\u043c\\u043e \\u0443\\u0441\\u0443\\u043d\\u0443\\u0442\\u0438 \\u0441\\u0438\\u043d\\u0434\\u0440\\u043e\\u043c \\u00ab\\u0433\\u043e\\u043b\\u043e\\u0434\\u0443\\u044e\\u0447\\u043e\\u0433\\u043e \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430\\u00bb \\u0442\\u0430 \\u0432\\u0432\\u0430\\u0436\\u0430\\u0454\\u043c\\u043e, \\u0449\\u043e \\u0434\\u043e\\u0441\\u0442\\u0443\\u043f\\u043d\\u0435 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u043e \\u0442\\u0430 \\u0443\\u0441\\u043f\\u0456\\u0445 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430 \\u043d\\u0435 \\u043f\\u043e\\u0432\\u0438\\u043d\\u043d\\u0456 \\u0432\\u0438\\u043a\\u043b\\u044e\\u0447\\u0430\\u0442\\u0438 \\u043e\\u0434\\u043d\\u0435 \\u043e\\u0434\\u043d\\u043e\\u0433\\u043e.\",\"uk\":\"\\u041c\\u0438 \\u0437\\u0430\\u043f\\u0443\\u0441\\u0442\\u0438\\u043b\\u0438 Artwex, \\u0449\\u043e\\u0431 \\u043a\\u0438\\u043d\\u0443\\u0442\\u0438 \\u0432\\u0438\\u043a\\u043b\\u0438\\u043a \\u0433\\u0430\\u043b\\u0443\\u0437\\u0456, \\u0449\\u043e\\u0431 \\u0437\\u0430\\u043f\\u0440\\u043e\\u043f\\u043e\\u043d\\u0443\\u0432\\u0430\\u0442\\u0438 \\u0447\\u0435\\u0441\\u043d\\u0438\\u0439 \\u0456 \\u0441\\u0442\\u0456\\u0439\\u043a\\u0438\\u0439 \\u0440\\u0438\\u043d\\u043e\\u043a \\u0434\\u043b\\u044f \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0456\\u0432, \\u0434\\u0435 \\u0431\\u0443\\u0434\\u044c-\\u0445\\u0442\\u043e \\u043c\\u043e\\u0436\\u0435 \\u0437\\u043d\\u0430\\u0439\\u0442\\u0438 \\u0442\\u0430 \\u043a\\u0443\\u043f\\u0438\\u0442\\u0438 \\u043e\\u0440\\u0438\\u0433\\u0456\\u043d\\u0430\\u043b\\u044c\\u043d\\u0435 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u043e. \\u041c\\u0438 \\u043f\\u0440\\u0430\\u0433\\u043d\\u0435\\u043c\\u043e \\u0443\\u0441\\u0443\\u043d\\u0443\\u0442\\u0438 \\u0441\\u0438\\u043d\\u0434\\u0440\\u043e\\u043c \\u00ab\\u0433\\u043e\\u043b\\u043e\\u0434\\u0443\\u044e\\u0447\\u043e\\u0433\\u043e \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430\\u00bb \\u0442\\u0430 \\u0432\\u0432\\u0430\\u0436\\u0430\\u0454\\u043c\\u043e, \\u0449\\u043e \\u0434\\u043e\\u0441\\u0442\\u0443\\u043f\\u043d\\u0435 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u043e \\u0442\\u0430 \\u0443\\u0441\\u043f\\u0456\\u0445 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430 \\u043d\\u0435 \\u043f\\u043e\\u0432\\u0438\\u043d\\u043d\\u0456 \\u0432\\u0438\\u043a\\u043b\\u044e\\u0447\\u0430\\u0442\\u0438 \\u043e\\u0434\\u043d\\u0435 \\u043e\\u0434\\u043d\\u043e\\u0433\\u043e.\"}', '2023-01-27 13:39:59', '2023-01-27 13:39:59');
INSERT INTO `language_lines` VALUES (158, 'about', 'p5', '{\"en\":\"\\\"Looking into one\'s own creativity, a person recognizes in himself what which is not available for perception at the rational level of consciousness, which is not amenable to precise interpretation.\\\"\",\"ru\":\"\\u00ab\\u0412\\u0434\\u0438\\u0432\\u043b\\u044f\\u044e\\u0447\\u0438\\u0441\\u044c \\u0443 \\u0432\\u043b\\u0430\\u0441\\u043d\\u0443 \\u0442\\u0432\\u043e\\u0440\\u0447\\u0456\\u0441\\u0442\\u044c, \\u043b\\u044e\\u0434\\u0438\\u043d\\u0430 \\u043f\\u0456\\u0437\\u043d\\u0430\\u0454 \\u0432 \\u0441\\u043e\\u0431\\u0456 \\u0442\\u0435, \\u0449\\u043e \\u043d\\u0435\\u0434\\u043e\\u0441\\u0442\\u0443\\u043f\\u043d\\u0435 \\u0434\\u043b\\u044f \\u0441\\u043f\\u0440\\u0438\\u0439\\u043d\\u044f\\u0442\\u0442\\u044f \\u043d\\u0430 \\u0440\\u0456\\u0432\\u043d\\u0456 \\u0440\\u0430\\u0446\\u0456\\u043e\\u043d\\u0430\\u043b\\u044c\\u043d\\u043e\\u0457 \\u0441\\u0432\\u0456\\u0434\\u043e\\u043c\\u043e\\u0441\\u0442\\u0456, \\u0449\\u043e \\u043d\\u0435 \\u043f\\u0456\\u0434\\u0434\\u0430\\u0454\\u0442\\u044c\\u0441\\u044f \\u0442\\u043e\\u0447\\u043d\\u0456\\u0439 \\u0456\\u043d\\u0442\\u0435\\u0440\\u043f\\u0440\\u0435\\u0442\\u0430\\u0446\\u0456\\u0457.\\u00bb\",\"uk\":\"\\u00ab\\u0412\\u0434\\u0438\\u0432\\u043b\\u044f\\u044e\\u0447\\u0438\\u0441\\u044c \\u0443 \\u0432\\u043b\\u0430\\u0441\\u043d\\u0443 \\u0442\\u0432\\u043e\\u0440\\u0447\\u0456\\u0441\\u0442\\u044c, \\u043b\\u044e\\u0434\\u0438\\u043d\\u0430 \\u043f\\u0456\\u0437\\u043d\\u0430\\u0454 \\u0432 \\u0441\\u043e\\u0431\\u0456 \\u0442\\u0435, \\u0449\\u043e \\u043d\\u0435\\u0434\\u043e\\u0441\\u0442\\u0443\\u043f\\u043d\\u0435 \\u0434\\u043b\\u044f \\u0441\\u043f\\u0440\\u0438\\u0439\\u043d\\u044f\\u0442\\u0442\\u044f \\u043d\\u0430 \\u0440\\u0456\\u0432\\u043d\\u0456 \\u0440\\u0430\\u0446\\u0456\\u043e\\u043d\\u0430\\u043b\\u044c\\u043d\\u043e\\u0457 \\u0441\\u0432\\u0456\\u0434\\u043e\\u043c\\u043e\\u0441\\u0442\\u0456, \\u04