INSERT INTO `language_lines` VALUES (166, 'orderart', 'p_1', '{\"en\":\"Can\'t find the perfect picture for the space above the fireplace? Looking for a portrait for studio decoration? Or maybe a piece of art has already caught your eye, but it\'s just not quite the right size or color. Do not worry. Our artists will help you create the work of your dreams.\",\"ru\":\"\\u041d\\u0435 \\u043c\\u043e\\u0436\\u0435\\u0442\\u0435 \\u0437\\u043d\\u0430\\u0439\\u0442\\u0438 \\u0456\\u0434\\u0435\\u0430\\u043b\\u044c\\u043d\\u0443 \\u043a\\u0430\\u0440\\u0442\\u0438\\u043d\\u0443 \\u0434\\u043b\\u044f \\u043f\\u0440\\u043e\\u0441\\u0442\\u043e\\u0440\\u0443 \\u043d\\u0430\\u0434 \\u043a\\u0430\\u043c\\u0456\\u043d\\u043e\\u043c? \\u0428\\u0443\\u043a\\u0430\\u0454\\u0442\\u0435 \\u043f\\u043e\\u0440\\u0442\\u0440\\u0435\\u0442 \\u0434\\u043b\\u044f \\u0434\\u0435\\u043a\\u043e\\u0440\\u0443 \\u0441\\u0442\\u0443\\u0434\\u0456\\u0457? \\u0410\\u0431\\u043e, \\u043c\\u043e\\u0436\\u043b\\u0438\\u0432\\u043e, \\u0432\\u0438\\u0442\\u0432\\u0456\\u0440 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430 \\u0432\\u0436\\u0435 \\u043f\\u0440\\u0438\\u0432\\u0435\\u0440\\u043d\\u0443\\u0432 \\u0432\\u0430\\u0448\\u0443 \\u0443\\u0432\\u0430\\u0433\\u0443, \\u0430\\u043b\\u0435 \\u0432\\u0456\\u043d \\u043d\\u0435 \\u0437\\u043e\\u0432\\u0441\\u0456\\u043c \\u0442\\u043e\\u0433\\u043e \\u0440\\u043e\\u0437\\u043c\\u0456\\u0440\\u0443 \\u0447\\u0438 \\u043a\\u043e\\u043b\\u044c\\u043e\\u0440\\u0443. \\u041d\\u0435 \\u0445\\u0432\\u0438\\u043b\\u044e\\u0439\\u0442\\u0435\\u0441\\u044f. \\u041d\\u0430\\u0448\\u0456 \\u043c\\u0438\\u0442\\u0446\\u0456 \\u0434\\u043e\\u043f\\u043e\\u043c\\u043e\\u0436\\u0443\\u0442\\u044c \\u0432\\u0430\\u043c \\u0437\\u0440\\u043e\\u0431\\u0438\\u0442\\u0438 \\u0432\\u0438\\u0442\\u0432\\u0456\\u0440, \\u043f\\u0440\\u043e \\u044f\\u043a\\u0438\\u0439 \\u0432\\u0438 \\u043c\\u0440\\u0456\\u044f\\u043b\\u0438.\",\"uk\":\"\\u041d\\u0435 \\u043c\\u043e\\u0436\\u0435\\u0442\\u0435 \\u0437\\u043d\\u0430\\u0439\\u0442\\u0438 \\u0456\\u0434\\u0435\\u0430\\u043b\\u044c\\u043d\\u0443 \\u043a\\u0430\\u0440\\u0442\\u0438\\u043d\\u0443 \\u0434\\u043b\\u044f \\u043f\\u0440\\u043e\\u0441\\u0442\\u043e\\u0440\\u0443 \\u043d\\u0430\\u0434 \\u043a\\u0430\\u043c\\u0456\\u043d\\u043e\\u043c? \\u0428\\u0443\\u043a\\u0430\\u0454\\u0442\\u0435 \\u043f\\u043e\\u0440\\u0442\\u0440\\u0435\\u0442 \\u0434\\u043b\\u044f \\u0434\\u0435\\u043a\\u043e\\u0440\\u0443 \\u0441\\u0442\\u0443\\u0434\\u0456\\u0457? \\u0410\\u0431\\u043e, \\u043c\\u043e\\u0436\\u043b\\u0438\\u0432\\u043e, \\u0432\\u0438\\u0442\\u0432\\u0456\\u0440 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430 \\u0432\\u0436\\u0435 \\u043f\\u0440\\u0438\\u0432\\u0435\\u0440\\u043d\\u0443\\u0432 \\u0432\\u0430\\u0448\\u0443 \\u0443\\u0432\\u0430\\u0433\\u0443, \\u0430\\u043b\\u0435 \\u0432\\u0456\\u043d \\u043d\\u0435 \\u0437\\u043e\\u0432\\u0441\\u0456\\u043c \\u0442\\u043e\\u0433\\u043e \\u0440\\u043e\\u0437\\u043c\\u0456\\u0440\\u0443 \\u0447\\u0438 \\u043a\\u043e\\u043b\\u044c\\u043e\\u0440\\u0443. \\u041d\\u0435 \\u0445\\u0432\\u0438\\u043b\\u044e\\u0439\\u0442\\u0435\\u0441\\u044f. \\u041d\\u0430\\u0448\\u0456 \\u043c\\u0438\\u0442\\u0446\\u0456 \\u0434\\u043e\\u043f\\u043e\\u043c\\u043e\\u0436\\u0443\\u0442\\u044c \\u0432\\u0430\\u043c \\u0437\\u0440\\u043e\\u0431\\u0438\\u0442\\u0438 \\u0432\\u0438\\u0442\\u0432\\u0456\\u0440, \\u043f\\u0440\\u043e \\u044f\\u043a\\u0438\\u0439 \\u0432\\u0438 \\u043c\\u0440\\u0456\\u044f\\u043b\\u0438.\"}', '2023-01-27 13:48:13', '2023-01-27 13:48:13');
INSERT INTO `language_lines` VALUES (167, 'orderart', 'p_2', '{\"en\":\"Leave your contact details and a description of your creativity below. Describe your budget, describe your ideal piece of art. We will find you an artist who will accept your proposal and contact you to discuss the detailed requirements. Once the order is accepted, you pay a deposit to get started, and when you\'re satisfied at the end, you pay the rest.\",\"ru\":\"\\u0417\\u0430\\u043b\\u0438\\u0448\\u0456\\u0442\\u044c \\u0441\\u0432\\u043e\\u0457 \\u043a\\u043e\\u043d\\u0442\\u0430\\u043a\\u0442\\u043d\\u0456 \\u0434\\u0430\\u043d\\u0456 \\u0442\\u0430 \\u043e\\u043f\\u0438\\u0441 \\u0432\\u0430\\u0448\\u043e\\u0457 \\u043a\\u0440\\u0435\\u0430\\u0442\\u0438\\u0432\\u043d\\u043e\\u0441\\u0442\\u0456 \\u043d\\u0438\\u0436\\u0447\\u0435.  \\u041e\\u043f\\u0438\\u0448\\u0456\\u0442\\u044c \\u0441\\u0432\\u0456\\u0439 \\u0431\\u044e\\u0434\\u0436\\u0435\\u0442, \\u043e\\u043f\\u0438\\u0448\\u0456\\u0442\\u044c \\u0441\\u0432\\u0456\\u0439 \\u0456\\u0434\\u0435\\u0430\\u043b\\u044c\\u043d\\u0438\\u0439 \\u0442\\u0432\\u0456\\u0440 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430. \\u041c\\u0438 \\u043f\\u0456\\u0434\\u0448\\u0443\\u043a\\u0430\\u0454\\u043c\\u043e \\u0432\\u0430\\u043c \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430, \\u044f\\u043a\\u0438\\u0439 \\u043f\\u0440\\u0438\\u0439\\u043c\\u0435 \\u0432\\u0430\\u0448\\u0443 \\u043f\\u0440\\u043e\\u043f\\u043e\\u0437\\u0438\\u0446\\u0456\\u044e \\u0442\\u0430 \\u0437\\u0432\\u2019\\u044f\\u0436\\u0435\\u0442\\u044c\\u0441\\u044f \\u0437 \\u0432\\u0430\\u043c\\u0438, \\u0449\\u043e\\u0431 \\u043e\\u0431\\u0433\\u043e\\u0432\\u043e\\u0440\\u0438\\u0442\\u0438 \\u0434\\u0435\\u0442\\u0430\\u043b\\u044c\\u043d\\u0456 \\u0432\\u0438\\u043c\\u043e\\u0433\\u0438. \\u041f\\u0456\\u0441\\u043b\\u044f \\u043f\\u0440\\u0438\\u0439\\u043d\\u044f\\u0442\\u0442\\u044f \\u0437\\u0430\\u043c\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f \\u0432\\u0438 \\u0441\\u043f\\u043b\\u0430\\u0447\\u0443\\u0454\\u0442\\u0435 \\u0434\\u0435\\u043f\\u043e\\u0437\\u0438\\u0442, \\u0449\\u043e\\u0431 \\u043f\\u043e\\u0447\\u0430\\u0442\\u0438 \\u0440\\u043e\\u0431\\u043e\\u0442\\u0443, \\u0430 \\u043a\\u043e\\u043b\\u0438 \\u0432\\u0438 \\u0431\\u0443\\u0434\\u0435\\u0442\\u0435 \\u0437\\u0430\\u0434\\u043e\\u0432\\u043e\\u043b\\u0435\\u043d\\u0456 \\u043f\\u0440\\u0438 \\u0437\\u0430\\u0432\\u0435\\u0440\\u0448\\u0435\\u043d\\u043d\\u0456, \\u0432\\u0438 \\u0441\\u043f\\u043b\\u0430\\u0447\\u0443\\u0454\\u0442\\u0435 \\u0440\\u0435\\u0448\\u0442\\u0443.\",\"uk\":\"\\u0417\\u0430\\u043b\\u0438\\u0448\\u0456\\u0442\\u044c \\u0441\\u0432\\u043e\\u0457 \\u043a\\u043e\\u043d\\u0442\\u0430\\u043a\\u0442\\u043d\\u0456 \\u0434\\u0430\\u043d\\u0456 \\u0442\\u0430 \\u043e\\u043f\\u0438\\u0441 \\u0432\\u0430\\u0448\\u043e\\u0457 \\u043a\\u0440\\u0435\\u0430\\u0442\\u0438\\u0432\\u043d\\u043e\\u0441\\u0442\\u0456 \\u043d\\u0438\\u0436\\u0447\\u0435.  \\u041e\\u043f\\u0438\\u0448\\u0456\\u0442\\u044c \\u0441\\u0432\\u0456\\u0439 \\u0431\\u044e\\u0434\\u0436\\u0435\\u0442, \\u043e\\u043f\\u0438\\u0448\\u0456\\u0442\\u044c \\u0441\\u0432\\u0456\\u0439 \\u0456\\u0434\\u0435\\u0430\\u043b\\u044c\\u043d\\u0438\\u0439 \\u0442\\u0432\\u0456\\u0440 \\u043c\\u0438\\u0441\\u0442\\u0435\\u0446\\u0442\\u0432\\u0430. \\u041c\\u0438 \\u043f\\u0456\\u0434\\u0448\\u0443\\u043a\\u0430\\u0454\\u043c\\u043e \\u0432\\u0430\\u043c \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a\\u0430, \\u044f\\u043a\\u0438\\u0439 \\u043f\\u0440\\u0438\\u0439\\u043c\\u0435 \\u0432\\u0430\\u0448\\u0443 \\u043f\\u0440\\u043e\\u043f\\u043e\\u0437\\u0438\\u0446\\u0456\\u044e \\u0442\\u0430 \\u0437\\u0432\\u2019\\u044f\\u0436\\u0435\\u0442\\u044c\\u0441\\u044f \\u0437 \\u0432\\u0430\\u043c\\u0438, \\u0449\\u043e\\u0431 \\u043e\\u0431\\u0433\\u043e\\u0432\\u043e\\u0440\\u0438\\u0442\\u0438 \\u0434\\u0435\\u0442\\u0430\\u043b\\u044c\\u043d\\u04