INSERT INTO `language_lines` VALUES (168, 'orderart', 'p_3', '{\"en\":\"Once your work is accepted, your artist will be with you every step of the way, and will provide regular updates.\",\"ru\":\"\\u041a\\u043e\\u043b\\u0438 \\u0432\\u0430\\u0448\\u0443 \\u0440\\u043e\\u0431\\u043e\\u0442\\u0443 \\u043f\\u0440\\u0438\\u0439\\u043c\\u0443\\u0442\\u044c, \\u0432\\u0430\\u0448 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a \\u0431\\u0443\\u0434\\u0435 \\u0437 \\u0432\\u0430\\u043c\\u0438 \\u043d\\u0430 \\u043a\\u043e\\u0436\\u043d\\u043e\\u043c\\u0443 \\u043a\\u0440\\u043e\\u0446\\u0456, \\u0442\\u0430 \\u0456\\u043d\\u0444\\u043e\\u0440\\u043c\\u0443\\u0432\\u0430\\u0442\\u0438\\u043c\\u0435 \\u0440\\u0435\\u0433\\u0443\\u043b\\u044f\\u0440\\u043d\\u0438\\u043c\\u0438 \\u043e\\u043d\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f\\u043c\\u0438.\",\"uk\":\"\\u041a\\u043e\\u043b\\u0438 \\u0432\\u0430\\u0448\\u0443 \\u0440\\u043e\\u0431\\u043e\\u0442\\u0443 \\u043f\\u0440\\u0438\\u0439\\u043c\\u0443\\u0442\\u044c, \\u0432\\u0430\\u0448 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a \\u0431\\u0443\\u0434\\u0435 \\u0437 \\u0432\\u0430\\u043c\\u0438 \\u043d\\u0430 \\u043a\\u043e\\u0436\\u043d\\u043e\\u043c\\u0443 \\u043a\\u0440\\u043e\\u0446\\u0456, \\u0442\\u0430 \\u0456\\u043d\\u0444\\u043e\\u0440\\u043c\\u0443\\u0432\\u0430\\u0442\\u0438\\u043c\\u0435 \\u0440\\u0435\\u0433\\u0443\\u043b\\u044f\\u0440\\u043d\\u0438\\u043c\\u0438 \\u043e\\u043d\\u043e\\u0432\\u043b\\u0435\\u043d\\u043d\\u044f\\u043c\\u0438.\"}', '2023-01-27 13:48:50', '2023-01-27 13:48:50');
