INSERT INTO `products` VALUES (7, 8, '{\"en\":\"Wave\",\"uk\":\"\\u0425\\u0432\\u0438\\u043b\\u044f\"}', '235243', '{\"en\":\"<p>The artwork \\\"The Wave\\\" was written as part of the project \\\"Immersion\\\", which the artist was preparing for March 2022, but the exhibition did not take place due to the beginning of the war.<br><\\/p>\",\"uk\":\"<p><span lang=\\\"UK\\\" style=\\\"color: rgb(0, 0, 0); font-size: 11pt; font-family: \\\"Times New Roman\\\", serif;\\\">\\u0425\\u0443\\u0434\\u043e\\u0436\\u043d\\u0456\\u0439 \\u0442\\u0432\\u0456\\u0440 \\u00ab\\u0425\\u0432\\u0438\\u043b\\u044f\\u00bb \\u043d\\u0430\\u043f\\u0438\\u0441\\u0430\\u043d\\u043e \\u0432 \\u0440\\u0430\\u043c\\u043a\\u0430\\u0445 \\u043f\\u0440\\u043e\\u0435\\u043a\\u0442\\u0443 \\u00ab\\u0417\\u0430\\u043d\\u0443\\u0440\\u0435\\u043d\\u043d\\u044f\\u00bb, \\u044f\\u043a\\u0438\\u0439 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a \\u0433\\u043e\\u0442\\u0443\\u0432\\u0430\\u0432 \\u043d\\u0430 \\u0431\\u0435\\u0440\\u0435\\u0437\\u0435\\u043d\\u044c \\u043c\\u0456\\u0441\\u044f\\u0446\\u044c 2022 \\u0440\\u043e\\u043a\\u0443, \\u0430\\u043b\\u0435 \\u0432\\u0438\\u0441\\u0442\\u0430\\u0432\\u043a\\u0430 \\u043d\\u0435 \\u0432\\u0456\\u0434\\u0431\\u0443\\u043b\\u0430\\u0441\\u044c \\u0447\\u0435\\u0440\\u0435\\u0437 \\u043f\\u043e\\u0447\\u0430\\u0442\\u043e\\u043a \\u0432\\u0456\\u0439\\u043d\\u0438.<\\/span><span style=\\\"color: rgb(0, 0, 0); font-size: medium;\\\"><\\/span><br><\\/p>\"}', '{\"en\":\"<p>The artwork \\\"The Wave\\\" was written as part of the project \\\"Immersion\\\", which the artist was preparing for March 2022, but the exhibition did not take place due to the beginning of the war. The predictions of scientists and the \\\"achievements\\\" of our civilization lead to global warming and further cataclysms. But here we are not even talking about a catastrophe on a planetary scale, but about the immersion of humanity in its desires, ambitions and fears. Once glamorous and pathos compilations turn into OOPS. And events shift emphasis and priorities, pushing us to a different worldview. Water washes away everything...<br><\\/p>\",\"uk\":\"<p class=\\\"MsoNormal\\\" style=\\\"margin: 0cm;\\\"><span lang=\\\"UK\\\" style=\\\"\\\" times=\\\"\\\" new=\\\"\\\" roman\\\",=\\\"\\\" serif;\\\"=\\\"\\\">\\u0425\\u0443\\u0434\\u043e\\u0436\\u043d\\u0456\\u0439 \\u0442\\u0432\\u0456\\u0440 \\u00ab\\u0425\\u0432\\u0438\\u043b\\u044f\\u00bb \\u043d\\u0430\\u043f\\u0438\\u0441\\u0430\\u043d\\u043e \\u0432 \\u0440\\u0430\\u043c\\u043a\\u0430\\u0445 \\u043f\\u0440\\u043e\\u0435\\u043a\\u0442\\u0443 \\u00ab\\u0417\\u0430\\u043d\\u0443\\u0440\\u0435\\u043d\\u043d\\u044f\\u00bb, \\u044f\\u043a\\u0438\\u0439 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u043a \\u0433\\u043e\\u0442\\u0443\\u0432\\u0430\\u0432 \\u043d\\u0430 \\u0431\\u0435\\u0440\\u0435\\u0437\\u0435\\u043d\\u044c \\u043c\\u0456\\u0441\\u044f\\u0446\\u044c 2022 \\u0440\\u043e\\u043a\\u0443, \\u0430\\u043b\\u0435 \\u0432\\u0438\\u0441\\u0442\\u0430\\u0432\\u043a\\u0430 \\u043d\\u0435 \\u0432\\u0456\\u0434\\u0431\\u0443\\u043b\\u0430\\u0441\\u044c \\u0447\\u0435\\u0440\\u0435\\u0437 \\u043f\\u043e\\u0447\\u0430\\u0442\\u043e\\u043a \\u0432\\u0456\\u0439\\u043d\\u0438. \\u041f\\u0440\\u043e\\u0433\\u043d\\u043e\\u0437\\u0438 \\u0432\\u0447\\u0435\\u043d\\u0438\\u0445 \\u0442\\u0430 \\u00ab\\u0434\\u043e\\u0441\\u044f\\u0433\\u043d\\u0435\\u043d\\u043d\\u044f\\u00bb \\u043d\\u0430\\u0448\\u043e\\u0457 \\u0446\\u0438\\u0432\\u0456\\u043b\\u0456\\u0437\\u0430\\u0446\\u0456\\u0457 \\u0432\\u0435\\u0434\\u0443\\u0442\\u044c \\u0434\\u043e \\u0433\\u043b\\u043e\\u0431\\u0430\\u043b\\u044c\\u043d\\u043e\\u0433\\u043e \\u043f\\u043e\\u0442\\u0435\\u043f\\u043b\\u0456\\u043d\\u043d\\u044f \\u0442\\u0430 \\u043f\\u043e\\u0434\\u0430\\u043b\\u044c\\u0448\\u0438\\u0445 \\u043a\\u0430\\u0442\\u0430\\u043a\\u043b\\u0456\\u0437\\u043c\\u0456\\u0432. \\u0410\\u043b\\u0435 \\u0442\\u0443\\u0