INSERT INTO `shops` VALUES (16, NULL, '{\"en\":\"Iryna\",\"uk\":\"\\u0406\\u0440\\u0438\\u043d\\u0430\"}', '{\"en\":\"Sushelnytska\",\"uk\":\"\\u0421\\u0443\\u0448\\u0435\\u043b\\u044c\\u043d\\u0438\\u0446\\u044c\\u043a\\u0430\"}', NULL, 'iryna', NULL, '{\"en\":\"<p>Sushelnytska Iryna Lubomyrivna. Ukrainian artist. She was born on January 30, 1976 in Kharkiv. From 1985 to 1990, she studied at a local art school. In 2004, she graduated from the Kharkiv Institute of Business and Management, and in 2009 from the art and graphic faculty of the Southern Ukrainian National Pedagogical University named after K.D. Ushinsky and received a master\'s degree with honors. Since 2001, he has been actively participating in exhibition activities both in Ukraine and abroad. He also holds numerous personal exhibitions of his works. The works of Iryna Lyubomirivna can be found in the Belgorod-Dniester Museum of Local Lore, in the Illichiv Museum of Fine Arts named after A.M. White, in private gatherings in Ukraine and abroad.<\\/p><p>The artist lives and works in Odessa.<\\/p>\",\"uk\":\"<p class=\\\"MsoNormal\\\" style=\\\"margin: 0cm; font-size: 12pt; font-family: Calibri, sans-serif; color: rgb(0, 0, 0);\\\"><span lang=\\\"UK\\\" style=\\\"font-size: 11pt; font-family: &quot;Source Sans Pro&quot;; background: rgb(254, 255, 255);\\\">\\u0421\\u0443\\u0448\\u0435\\u043b\\u044c\\u043d\\u0438\\u0446\\u044c\\u043a\\u0430 \\u0406\\u0440\\u0438\\u043d\\u0430 \\u041b\\u044e\\u0431\\u043e\\u043c\\u0438\\u0440\\u0456\\u0432\\u043d\\u0430. \\u0423\\u043a\\u0440\\u0430\\u0457\\u043d\\u0441\\u044c\\u043a\\u0430 \\u0445\\u0443\\u0434\\u043e\\u0436\\u043d\\u0438\\u0446\\u044f. \\u041d\\u0430\\u0440\\u043e\\u0434\\u0438\\u043b\\u0430\\u0441\\u044c 30 \\u0441\\u0456\\u0447\\u043d\\u044f 1976 \\u0440\\u043e\\u043a\\u0443 \\u0443 \\u043c\\u0456\\u0441\\u0442\\u0456 \\u0425\\u0430\\u0440\\u043a\\u043e\\u0432\\u0456. \\u0417 <\\/span><span lang=\\\"UK\\\" style=\\\"font-size: 11pt; font-family: &quot;T