$(document).ready(function() {
	setPageScriptSettings()
});

function setPageScriptSettings(){
	setSortDropdownsWidth()

	$('.items').masonry({
	  // options
	  itemSelector: '.item',
	  columnWidth: 0
	});
}

addEventListener("resize", (event) => {
	setPageScriptSettings()
});


function setSortDropdownsWidth(){
	let catalogItem = document.querySelector('.product-image')
	catalogItemWidth = ''
	if(catalogItem){
		catalogItemWidth =  catalogItem.offsetWidth
		if(catalogItemWidth && catalogItemWidth > 0){
			let dropdownRight = document.querySelector('.dropdown-right')
			let dropdownLeft = document.querySelector('.dropdown-left')
			dropdownRight.style.maxWidth = catalogItemWidth +'px'
			dropdownLeft.style.maxWidth = catalogItemWidth +'px'
		}
	}
}

// document.getElementsByTagName('body')[0].clientWidth

	
	let bodyWidth = document.getElementsByTagName('body')[0].clientWidth
	if(bodyWidth < 992){
		let filterBtn = document.querySelector('.showFilterButton')
		let showFilterButtonTitle = document.querySelector('.showFilterButtonTitle')
		
		let isActive = filterBtn.classList.contains('--active');

		let filterBlock = document.querySelector('.filter-block')

		if(isActive){
			filterBtn.classList.remove('--active');
			showFilterButtonTitle.innerHTML = 'Показать фильтры'
			filterBlock.classList.add('d-none');
		}

	}

function showFilterHandler(){
	let filterBtn = document.querySelector('.showFilterButton')
	let showFilterButtonTitle = document.querySelector('.showFilterButtonTitle')
	
	let isActive = filterBtn.classList.contains('--active');

	let filterBlock = document.querySelector('.filter-block')

	
	let body = document.getElementsByTagName('body')[0]
	let bodyWidth = body.clientWidth

	if(isActive){
		filterBtn.classList.remove('--active');
		showFilterButtonTitle.innerHTML = 'Показать фильтры'
		filterBlock.classList.add('d-none');

		if(bodyWidth < 992){
			console.log('asdasdad');
			document.getElementsByTagName('body')[0].classList.add('body-lock')
		}
	}else{
		filterBtn.classList.add('--active');
		showFilterButtonTitle.innerHTML = 'Скрыть фильтры'
		filterBlock.classList.remove('d-none');
		if(bodyWidth < 992){
			console.log('qweqweqwe');
			document.getElementsByTagName('body')[0].classList.add('body-lock')
		}
	}


	setPageScriptSettings()
}


